import React, { useState } from "react";
import Sudoku from "./Sudoku";
import Interface from "./Interface";

function App() {
  // State to manage the current language
  const [language, setLanguage] = useState('en');

  // Function to toggle between English and Japanese
  const toggleLanguage = () => {
    setLanguage(prevLanguage => prevLanguage === 'en' ? 'jp' : 'en');
  };

  // Example handleInterface function
  const handleInterface = (action) => {
    console.log(`Action received: ${action}`);
    // Add logic for different actions (create, validate, solve, clear)
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>{language === 'en' ? '' : 'すうどく'}</h2>
        <h1>{language === 'en' ? 'Sūdoku' : '数独'}</h1>
        {/* Language button in the header */}
        <button className="language-button" onClick={toggleLanguage}>
          {language === 'en' ? 'JP' : 'EN'}
        </button>
      </header>
      <div className="sudoku-container">
        <Sudoku />
        <Interface handleInterface={handleInterface} language={language} status="Idle" />
      </div>
    </div>
  );
}

export default App;
